sources.main.scss:
@import url('https://fonts.googleapis.com/css2?family=Capriola&family=Roboto:wght@400;500;700&display=swap');

// Color system
$black: #333333 !default;
$gray: #717171 !default;
$yellow: #FBAA2A !default;
$lightyellow: #FFE1AB !default;
$lighteryellow: #fff7e8 !default;

body {
  background-color: $lightyellow;
}

h1,
h2,
h3,
span {
  font-family: 'Capriola', sans-serif;

  color: $black;
}

p {
  font-family: 'Roboto', sans-serif;
}

.container {
  margin: 0 auto;
  max-width: 600px;
  padding: 32px 16px;

  .dropdown {
    display: flex;
    flex-direction: column;
    border: 2px solid $black;
    box-shadow: $black 8px 8px 0 0;
    background-color: $yellow;
    border-radius: 8px;
    max-height: 90vh;
    transition: max-height .3s ease-in-out;
    margin-bottom: 16px;
    overflow: hidden;

    &.collapsed {
      max-height: 56px;
    }

    .dropdown-title {
      display: flex;
      justify-content: space-between;
      align-items: center;
      cursor: pointer;
      padding: 8px 16px;
      border-bottom: 2px solid $black;

      h2 {
        padding-left: 8px;
      }

      span {
        display: inline-block;
        font-size: 40px;
        transition: transform 0.2s ease;
        transform: rotate(180deg);
      }

      &.collapsed {
        span {
          transform: rotate(0);
        }
      }
    }

    .content {
      display: flex;
      overflow: hidden;
      padding: 16px;
      background-color: $lighteryellow;


      &:has(>.scooters) {
        padding: 0;
      }

      .scooters {
        width: 100%;
        position: relative;

        .swiper-container {
          height: 100%;
          overflow: hidden;
          .swiper-wrapper {
            .swiper-slide {
              padding: 16px;
              box-sizing: border-box;

              .scootercard {
                width: 100%;
                display: flex;
                flex-direction: column;
                border: 2px solid $black;
                background-color: $lightyellow;
                border-radius: 8px;
                overflow-x: hidden;
                overflow-y: scroll;
                margin-top: 40px;
                height: calc(100% - 90px);


                &::-webkit-scrollbar {
                  display: none;
                }

                p {
                  color: $gray;
                  font-weight: 700;
                  line-height: 18px;
                }

                .imgwrap {
                  width: 100%;
                  padding-top: 65%;
                  border-bottom: 2px solid $black;
                  background-repeat: no-repeat;
                  background-size: cover;
                  background-attachment:fixed;
                  background-position: center;
                  &.lime {
                    background-image: url('./assets/image\ 6.png');
                  }
                  &.spin {
                    background-image: url('./assets/image\ 8.png');
                  }
                  &.bird {
                    background-image: url('./assets/image\ 7.png');
                  }
                }

                >div {
                  padding: 8px;
                }

                .scooterdetails {
                  display: flex;
                  flex-direction: column;
                  align-items: flex-start;
                  gap: 8px;

                  .scooter-name-price {
                    margin-bottom: 4px;

                    h3 {
                      font-size: 32px;
                      margin-bottom: 8px;
                    }
                  }

                  .downloadlinks {
                    display: flex;
                    gap: 8px;
                    align-self: stretch;

                    a {
                      display: flex;
                      width: 100%;
                      padding: 4px 8px;
                      justify-content: center;
                      align-items: center;
                      gap: 10px;
                      text-decoration: none;
                      border-radius: 8px;
                      border: 2px solid $black;
                      background: $yellow;

                      img {
                        width: 24px;
                      }

                      span {
                        color: #333;
                        text-align: center;
                        font-family: Capriola;
                        font-size: 12px;
                        font-style: normal;
                        font-weight: 400;
                        line-height: normal;
                      }
                    }
                  }

                  .available-passes {
                    display: flex;
                    flex-direction: column;
                    gap: 8px;
                    align-self: stretch;

                    .pass {
                      display: flex;
                      padding: 8px;
                      flex-direction: column;
                      align-items: flex-start;
                      gap: 16px;
                      align-self: stretch;
                      border-radius: 8px;
                      border: 2px solid $black;
                      background-color: $lighteryellow;

                      .pass-name-price {
                        display: flex;
                        justify-content: space-between;
                        align-items: center;
                        align-self: stretch;

                        p {
                          color: $black;
                        }
                      }

                      ul.pass-details {
                        li {
                          margin-bottom: 4px;

                          p {
                            color: $black;
                            font-weight: 400;
                          }
                        }
                      }
                    }
                  }
                }
              }
            }
          }
        }

        .swiper-pagination {
          position: absolute;
          top: 20px;
          left:0;
          right:0;
          margin: 0 auto;
          display: flex;
          justify-content: center;
          padding: 0 16px;
          gap:8px;

          .swiper-pagination-bullet {
            padding:4px 8px;
            width: 100%;
            height: auto;
            border:2px solid $black;
            background-color:$lightyellow;
            border-radius: 8px;
            opacity: 1;

            &.swiper-pagination-bullet-active {
              background-color: $yellow;
            }
            &:nth-of-type(1)::after {
              content:"Lime";
            }
            &:nth-of-type(2)::after {
              content:"Spin";
            }
            &:nth-of-type(3)::after {
              content:"Bird";
            }
          }
        }

        .swiper-prev, .swiper-next {
          z-index: 1;
          position: absolute;
          width: 48px;
          height: 48px;
          left:0;
          right:0;
          bottom: 8px;
          margin:0 auto;
          cursor: pointer;

          span {
            text-align: center;
            font-size: 48px;
          }
        }

        .swiper-prev {
          transform: translateX(-40px);
        }
        .swiper-next {
          transform: translateX(40px);
        }
      }
    }
  }
}
